import React from 'react';
import './Login.css';
import {styled} from 'styled-components';

const Login = () => {
    const Login = (userName, pass) =>{
        /* code gi do de noi toi api trong cai function nay */
    }
    const submit = () => {
        const textBox = document.getElementById('User-name');
        const userName = textBox.value;

        const password = document.getElementById('password');
        const pass = password.value;
        Login(userName, pass);
    }
    return (
        <div className='body' style='
        display: grid;
        height: 100vh;
        align-items: center;
        justify-content: center;
        '>
            <div className='container' style={{
                display: "flex",
                flexDirection: column,
                justifyContent: center,
                alignItems: center,
                width: 300,
                height: 350,
                border: 2,
                borderStyle: solid,

            }}>
                <h1 className='title' style='
                    margin-bottom: 3px;
                '
                >Login</h1>
                <input type="text" placeholder='Email or phone number' className='User-name' id='User-name'style='
                    font-size: 14px;
                    width: 250px;
                    height: 30px;
                    padding: 6px;
                    margin: 3px;
                '
                ></input>
                <input type='password' placeholder='Password' className='password' id='password' style='
                    font-size: 14px;
                    width: 250px;
                    height: 30px;
                    padding: 6px;
                    margin: 3px;
                '></input>
                <button type='submit' onClick={submit} className='submit-button' style='
                    width: 100px;
                    height: 30px;
                    background-color: lightblue;
                    border: 0;
                    transition: opacity 0.15s;
                    cursor: pointer;
                '>Log in</button>
            </div>
        </div>
    );
};

export default Login;
